$(function () {
  let e =
      /Chrome/.test(navigator.userAgent) || /Google Inc/.test(navigator.vendor),
    t = /safari/.test(navigator.userAgent.toLowerCase()),
    o = /iphone/.test(navigator.userAgent.toLowerCase());
  $(window).on("load", function () {
    $("#modal-fullscreen-xl").modal("show"),
      $("#modal-fullscreen-xl").addClass("fade"),
      e || (document.getElementById("myAudio").autoplay = !0),
      (t || o) && (document.getElementById("myAudio").autoplay = !1);
  }),
    AOS.init();
  $(".weddingDate").html("Sabtu, 7 Januari 2023");
  $(".venueAddress").html(
    "Balai Prajurit Ardhya Loka, Halim Perdana Kusuma, Jakarta Timur"
  );
  var a = new Date("Jan 7, 2023 10:00:00").getTime(),
    n = setInterval(function () {
      var e = new Date().getTime(),
        t = a - e,
        o = Math.floor(t / 864e5),
        i = Math.floor((t % 864e5) / 36e5),
        l = Math.floor((t % 36e5) / 6e4),
        s = Math.floor((t % 6e4) / 1e3);
      $("#day").html(o),
        $("#hour").html(i),
        $("#minute").html(l),
        $("#second").html(s),
        t < 0 && clearInterval(n);
    }, 1e3),
    i = !0,
    l = document.getElementById("myAudio");
  $("#audio").on("click", function (e) {
    e.preventDefault(),
      i
        ? ($("#play").removeClass("audio-button"),
          $("#pause").addClass("audio-button"),
          l.pause(),
          (i = !1))
        : ($("#play").addClass("audio-button"),
          $("#pause").removeClass("audio-button"),
          l.play(),
          (i = !0));
  }),
    $("#open-invitation").on("click", function (a) {
      a.preventDefault(),
        (document.body.scrollTop = 0),
        (document.documentElement.scrollTop = 0),
        (e || o || t) && (l.play(), (i = !0));
    });
  var s = window.location.href;
  (s = (s = s.split("?to=")[1])
    ? (function (e) {
        var t = e.toLowerCase().split(" ");
        if (-1 != e.indexOf("_")) t = e.toLowerCase().split("_");
        if (-1 != e.indexOf("-")) t = e.toLowerCase().split("-");
        if (-1 != e.indexOf("+")) t = e.toLowerCase().split("+");
        for (var o = 0; o < t.length; o++)
          t[o] = t[o].charAt(0).toUpperCase() + t[o].substring(1);
        return t.join(" ");
      })(s)
    : "Di Tempat"),
    $(".your-class").slick({
      dots: !0,
      infinite: !0,
      slidesToShow: 1,
      slidesToScroll: 1,
      centerMode: !0,
      adaptiveHeight: !0,
      variableWidth: !0,
      responsive: [
        {
          breakpoint: 815,
          settings: { arrows: !1, centerMode: !0, slidesToShow: 1 },
        },
      ],
    });
  new Typed("#guest-name", {
    strings: [s],
    typeSpeed: 100,
    backSpeed: 50,
    loop: !0,
  });
});
